import React, { useState } from 'react';
import './App.css';

const weatherApi = {
  key: "01877eb0f113d4d0c163508453b2d61e",
  base: "https://api.openweathermap.org/data/2.5/"
};

const timeApiBase = "https://timeapi.io/api/Time/current/coordinate?";

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});
  const [localTime, setLocalTime] = useState('');
  const [isFetchingTime, setIsFetchingTime] = useState(false); // New state to track fetching status

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${weatherApi.base}weather?q=${query}&units=metric&APPID=${weatherApi.key}`)
        .then(res => res.json())
        .then(result => {
          if (result.cod !== "404") {
            setWeather(result);
            setQuery('');
            setIsFetchingTime(true); // Start fetching local time
            fetchLocalTime(result.coord.lat, result.coord.lon);
          } else {
            console.error("City not found");
          }
        })
        .catch(err => {
          console.error("Error fetching weather data: ", err);
        });
    }
  };

  const fetchLocalTime = (lat, lon) => {
    fetch(`${timeApiBase}latitude=${lat}&longitude=${lon}`)
      .then(res => res.json())
      .then(result => {
        setLocalTime(result.dateTime);
        setIsFetchingTime(false); // Stop fetching when the time is fetched
      })
      .catch(err => {
        console.error("Error fetching local time: ", err);
        setIsFetchingTime(false); // Stop fetching if there is an error
      });
  };

  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  return (
    <div className={(typeof weather.main !== "undefined") ? ((weather.main.temp > 16) ? 'app warm' : 'app cold') : 'app'}>
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>

        {(typeof weather.main !== "undefined") && (
          <div className="weather">
            <div className="location">{weather.name}, {weather.sys.country}</div>
            <div className="date">{dateBuilder(new Date())}</div>
            <div className="weather-box">
              <div className="temp">
                {Math.round(weather.main.temp)}Â°C
              </div>
              <div className="weather">{weather.weather[0].main}</div>
            </div>
          </div>
        )}

        {/* Local Time Section (only appears after a search is made) */}
        {(typeof weather.main !== "undefined") && (
          <div className="local-time-box">
            <div className="local-time">
              Local Time: {isFetchingTime ? 'Fetching...' : (localTime ? new Date(localTime).toLocaleTimeString() : 'Error fetching time')}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
